export const COLORS = {
  // Primary colors
  primary: '#2563eb',      // Blue 600
  secondary: '#4f46e5',    // Indigo 600

  // Background colors
  background: '#ffffff',    // White
  surface: '#f8fafc',      // Slate 50
  inputBackground: '#f8fafc', // Slate 50

  // Text colors
  text: '#1e293b',         // Slate 800
  textLight: '#64748b',    // Slate 500
  textInverse: '#ffffff',  // White

  // Status colors
  success: '#22c55e',      // Green 500
  error: '#ef4444',        // Red 500
  warning: '#f59e0b',      // Amber 500
  info: '#3b82f6',         // Blue 500
  online: '#22c55e',       // Green 500
  offline: '#ef4444',      // Red 500

  // UI elements
  border: '#e2e8f0',       // Slate 200
  disabled: '#f1f5f9',     // Slate 100
  white: '#ffffff',        // White

  // Toner colors
  cyan: '#06b6d4',
  magenta: '#db2777',
  yellow: '#eab308',
  black: '#171717',
};

export const SIZES = {
  // Font sizes
  h1: 24,
  h2: 20,
  h3: 18,
  body1: 16,
  body2: 14,
  caption: 12,
  small: 10,

  // Icon sizes
  icon: {
    tiny: 12,
    small: 16,
    medium: 24,
    large: 32,
    xlarge: 48,
    xxlarge: 64,
  },

  // Padding
  padding: {
    xsmall: 4,
    small: 8,
    medium: 12,
    large: 16,
    xlarge: 24,
  },

  // Margins
  margin: {
    xsmall: 4,
    small: 8,
    medium: 12,
    large: 16,
    xlarge: 24,
  },

  // Border radius
  radius: {
    small: 4,
    medium: 8,
    large: 12,
    xlarge: 16,
  },

  // Component sizes
  buttonHeight: 48,
  inputHeight: 48,
  cardRadius: 12,
  borderRadius: 8,
  small: 8,
};

export const FONTS = {
  // Headings
  h1: {
    fontSize: SIZES.h1,
    fontWeight: '700',
    lineHeight: SIZES.h1 * 1.4,
  },
  h2: {
    fontSize: SIZES.h2,
    fontWeight: '600',
    lineHeight: SIZES.h2 * 1.4,
  },
  h3: {
    fontSize: SIZES.h3,
    fontWeight: '600',
    lineHeight: SIZES.h3 * 1.4,
  },

  // Body text
  body1: {
    fontSize: SIZES.body1,
    fontWeight: '400',
    lineHeight: SIZES.body1 * 1.5,
  },
  body2: {
    fontSize: SIZES.body2,
    fontWeight: '400',
    lineHeight: SIZES.body2 * 1.5,
  },

  // Other text styles
  button: {
    fontSize: SIZES.body2,
    fontWeight: '600',
    lineHeight: SIZES.body2 * 1.5,
  },
  caption: {
    fontSize: SIZES.caption,
    fontWeight: '400',
    lineHeight: SIZES.caption * 1.5,
  },
  label: {
    fontSize: SIZES.body2,
    fontWeight: '500',
    lineHeight: SIZES.body2 * 1.5,
  },

  // Font weights
  regular: {
    fontWeight: '400',
  },
  medium: {
    fontWeight: '500',
  },
  semibold: {
    fontWeight: '600',
  },
  bold: {
    fontWeight: '700',
  },
};

export const SHADOWS = {
  small: {
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 3,
    elevation: 2,
  },
  medium: {
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowOpacity: 0.15,
    shadowRadius: 6,
    elevation: 4,
  },
  large: {
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.2,
    shadowRadius: 8,
    elevation: 6,
  },
};

export default {
  COLORS,
  SIZES,
  FONTS,
  SHADOWS,
};
