import React, { useState, useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  KeyboardAvoidingView,
  Platform,
  ScrollView,
  Alert,
} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { COLORS, SIZES, FONTS } from '../utils/theme';
import CustomButton from '../components/CustomButton';
import InputField from '../components/InputField';
import authService from '../services/AuthService';
import { isValidPhoneNumber } from '../utils/validators';
import errorHandler from '../utils/errorHandler';

const AuthenticationScreen = ({ navigation }) => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [isVerifying, setIsVerifying] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const recaptchaContainerRef = useRef('recaptcha-container');

  useEffect(() => {
    checkAuthentication();
    initializeRecaptcha();
  }, []);

  const checkAuthentication = async () => {
    try {
      const isAuthenticated = await authService.isAuthenticated();
      if (isAuthenticated) {
        navigation.replace('MainTabs');
      }
    } catch (error) {
      errorHandler.handleError(error, {
        component: 'AuthenticationScreen',
        action: 'checkAuthentication',
      });
    }
  };

  const initializeRecaptcha = () => {
    try {
      authService.initRecaptcha(recaptchaContainerRef.current);
    } catch (error) {
      errorHandler.handleError(error, {
        component: 'AuthenticationScreen',
        action: 'initializeRecaptcha',
      });
    }
  };

  const handleSendVerificationCode = async () => {
    try {
      setError('');
      setLoading(true);

      if (!isValidPhoneNumber(phoneNumber)) {
        setError('Please enter a valid phone number');
        return;
      }

      await authService.initiatePhoneVerification(phoneNumber);
      setIsVerifying(true);
    } catch (error) {
      const errorDetails = errorHandler.handleError(error, {
        component: 'AuthenticationScreen',
        action: 'handleSendVerificationCode',
      });
      setError(errorDetails.message);
      // Re-initialize reCAPTCHA if there was an error
      initializeRecaptcha();
    } finally {
      setLoading(false);
    }
  };

  const handleVerifyCode = async () => {
    try {
      setError('');
      setLoading(true);

      if (!verificationCode || verificationCode.length !== 6) {
        setError('Please enter a valid verification code');
        return;
      }

      await authService.verifyCode(verificationCode);
      navigation.replace('MainTabs');
    } catch (error) {
      const errorDetails = errorHandler.handleError(error, {
        component: 'AuthenticationScreen',
        action: 'handleVerifyCode',
      });
      setError(errorDetails.message);
    } finally {
      setLoading(false);
    }
  };

  const handleResendCode = async () => {
    try {
      setError('');
      setLoading(true);
      // Re-initialize reCAPTCHA before resending code
      initializeRecaptcha();
      await authService.initiatePhoneVerification(phoneNumber);
      Alert.alert('Success', 'Verification code has been resent');
    } catch (error) {
      const errorDetails = errorHandler.handleError(error, {
        component: 'AuthenticationScreen',
        action: 'handleResendCode',
      });
      setError(errorDetails.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <KeyboardAvoidingView
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        style={styles.keyboardAvoid}
      >
        <ScrollView
          contentContainerStyle={styles.scrollContent}
          keyboardShouldPersistTaps="handled"
        >
          <View style={styles.content}>
            <Text style={styles.title}>
              {isVerifying ? 'Verify Your Number' : 'Welcome'}
            </Text>
            <Text style={styles.subtitle}>
              {isVerifying
                ? 'Enter the verification code sent to your phone'
                : 'Enter your phone number to continue'}
            </Text>

            {!isVerifying ? (
              <InputField
                label="Phone Number"
                value={phoneNumber}
                onChangeText={setPhoneNumber}
                placeholder="+1234567890"
                keyboardType="phone-pad"
                autoCapitalize="none"
                error={error}
                disabled={loading}
                required
              />
            ) : (
              <InputField
                label="Verification Code"
                value={verificationCode}
                onChangeText={setVerificationCode}
                placeholder="Enter 6-digit code"
                keyboardType="number-pad"
                maxLength={6}
                error={error}
                disabled={loading}
                required
              />
            )}

            <View style={styles.buttonContainer}>
              {!isVerifying ? (
                <CustomButton
                  title="Send Verification Code"
                  onPress={handleSendVerificationCode}
                  loading={loading}
                  disabled={!phoneNumber || loading}
                />
              ) : (
                <>
                  <CustomButton
                    title="Verify Code"
                    onPress={handleVerifyCode}
                    loading={loading}
                    disabled={!verificationCode || loading}
                  />
                  <CustomButton
                    title="Resend Code"
                    onPress={handleResendCode}
                    variant="outline"
                    style={styles.resendButton}
                    disabled={loading}
                  />
                </>
              )}
            </View>

            {/* Hidden reCAPTCHA container */}
            <View
              ref={recaptchaContainerRef}
              style={styles.recaptchaContainer}
            />
          </View>
        </ScrollView>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: COLORS.background,
  },
  keyboardAvoid: {
    flex: 1,
  },
  scrollContent: {
    flexGrow: 1,
  },
  content: {
    flex: 1,
    padding: SIZES.padding.large,
    justifyContent: 'center',
  },
  title: {
    ...FONTS.h1,
    color: COLORS.text,
    marginBottom: SIZES.padding.small,
    textAlign: 'center',
  },
  subtitle: {
    ...FONTS.body1,
    color: COLORS.textLight,
    marginBottom: SIZES.padding.xlarge,
    textAlign: 'center',
  },
  buttonContainer: {
    marginTop: SIZES.padding.large,
  },
  resendButton: {
    marginTop: SIZES.padding.medium,
  },
  recaptchaContainer: {
    position: 'absolute',
    width: 0,
    height: 0,
    opacity: 0,
  },
});

export default AuthenticationScreen;
